import React, {useEffect} from 'react';
import {addToCart} from '../actions/cartActions';
import {useDispatch, useSelector} from 'react-redux';
import {Link} from 'react-router-dom';
import {removeFromCart} from '../actions/cartActions';
import CheckoutSteps from '../components/CheckoutSteps'

function PlaceOrderScreen(props) {
    const cart = useSelector(state => state.cart);

const {cartItems, shipping, payment} = cart;
if(!shipping) {
    props.location.push("/shipping")
} 
if(!payment) {
    props.location.push("/payment")
} 
const qty = props.location.search? Number(props.location.search.split("=")[1]):1;
const dispatch = useDispatch();
const removeFromCartHandler = (productId) => {
    dispatch(removeFromCart(productId));
}

useEffect(() => {
    if(productId) {
        dispatch(addToCart(productId, qty))
    }
}, []);

const checkoutHandler = () => {
    props.history.push("/signin?redirect=shipping");
}
    return <div>
    <CheckoutSteps step1 step2 step3 step4  ></CheckoutSteps>
    <div className = "placeorder">
        <div className = "placeorder-info">
            <div>
                <h3>Shipping</h3>
                <div>
                    {cart.shipping.address}, {cart.shipping.city}, {cart.shipping.postalCode}, {cart.shipping.country}
                </div>
                <div>
                    <h3>Payment</h3>
                    <div>
                        Payment Method: {cart.payment.paymentMethod}
                    </div>
                </div>
                <div>
                <ul className = "cart-list-container">
    <li>
        <h3>
            Shopping Cart
        </h3>
        <div>
            Price
        </div>
    </li>
    {
        cartItems.length === 0 ?
        <div>
            Cart is empty
        </div>
        :
        cartItems.map( item => 
            <li>
                <div className = "cart-image">
                <img src = {item.image} alt = "product" />
                </div>
                <div className = "cart-name">
            <div>
                <Link to ={"/product/" + item.product}>
                {item.name}
                </Link>
            </div>
            <div>
                Qty:
                <select value ={item.qty} onChange={(e) => addToCart(item.product, e.target.value)}>
                    <option value = "1">1</option>
                    <option value = "2">2</option>
                    <option value = "3">3</option>
                    <option value = "4">4</option>
                </select>
                <button type = "button" className = "button-primary" onClick={() => removeFromCartHandler(item.product)}>
                    Delete
                </button>
            </div>
                </div>
                <div className = "cart-price">
                    ${item.price}
                </div>
            </li>
        )
    }
        </ul>
                </div>
            </div>

        </div>
        <div className = "placeorder-action">
            <h3>
                Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items)
                :
             ${cartItems.reduce((a, c)=> a+c.price * c.qty, 0 ) }
            </h3>
            <button onClick ={checkoutHandler} className = "button full-width" disabled = {cartItems.length === 0}>
                Proceed to Checkout 
            </button>
        </div>
    </div>
    </div>
}

export default PlaceOrderScreen;
